mapper.select:
select
  id, project_type, project_name, project_amount, project_leader, start_date, end_date, complete_time, acceptance_department, acceptance_report, task_source, project_area, scale, sheet_number, awards_department, prize_level, project_quality_approval, winning_time, acceptance_time, basic_content, credit_status, submit_status, user_id, schedule, create_time, modify_time, memo, status, audit_status, results_status, coordinate, meridian, coordinate_system, management_level, funding_source, entrust_unit, accept_unit, mobile_phone, phone, address, center_point, synchronization_status, contract_time
from two_project.t_mapping_project


        and id = #{id}


        and project_type = #{projectType}


        and project_name = #{projectName}


        and project_amount = #{projectAmount}


        and project_leader = #{projectLeader}


        and start_date = #{startDate}


        and end_date = #{endDate}


        and complete_time = #{completeTime}


        and acceptance_department = #{acceptanceDepartment}


        and acceptance_report = #{acceptanceReport}


        and task_source = #{taskSource}


        and project_area = #{projectArea}


        and scale = #{scale}


        and sheet_number = #{sheetNumber}


        and awards_department = #{awardsDepartment}


        and prize_level = #{prizeLevel}


        and project_quality_approval = #{projectQualityApproval}


        and winning_time = #{winningTime}


        and acceptance_time = #{acceptanceTime}


        and basic_content = #{basicContent}


        and credit_status = #{creditStatus}


        and submit_status = #{submitStatus}


        and user_id = #{userId}


        and schedule = #{schedule}


        and create_time = #{createTime}


        and modify_time = #{modifyTime}


        and memo = #{memo}


        and status = #{status}


        and audit_status = #{auditStatus}


        and results_status = #{resultsStatus}


        and coordinate = #{coordinate}


        and meridian = #{meridian}


        and coordinate_system = #{coordinateSystem}


        and management_level = #{managementLevel}


        and funding_source = #{fundingSource}


        and entrust_unit = #{entrustUnit}


        and accept_unit = #{acceptUnit}


        and mobile_phone = #{mobilePhone}


        and phone = #{phone}


        and address = #{address}


        and center_point = #{centerPoint}


        and synchronization_status = #{synchronizationStatus}


        and contract_time = #{contractTime}
